/**
 * Run all callbacks from storage
 */
export function runStorageCallbacks(storedItem, force = false) {
    // Get data
    const data = storedItem.data;
    if (!data && !force) {
        return;
    }
    // Update time
    storedItem.lastUsed = Date.now();
    // Run all callbacks
    let callback;
    while ((callback = storedItem.callbacks.shift())) {
        callback(data || null);
    }
}
