import { rm } from 'node:fs/promises';
import { appConfig } from '../../config/app.js';
/**
 * Remove old cache
 */
export async function cleanupStorageCache(storage) {
    const dir = storage.config.cacheDir.replace('{cache}', appConfig.cacheRootDir);
    try {
        await rm(dir, {
            recursive: true,
        });
    }
    catch {
        //
    }
}
