import { importIconSetFromJSONPackage } from '../common/json-package.js';
/**
 * Create importer for `@iconify-json/*` package
 */
export function createJSONPackageIconSetImporter(instance, options) {
    const obj = instance;
    const prefix = options.prefix;
    // Set static data
    const baseData = {
        type: 'icon-set',
        prefix,
    };
    Object.assign(obj, baseData);
    // Load data
    obj._loadDataFromDirectory = (path) => importIconSetFromJSONPackage(prefix, path, options);
    return obj;
}
