import { importIconSetFromJSON } from '../common/icon-set-json.js';
/**
 * Create importer for .json file
 */
export function createJSONIconSetImporter(instance, options) {
    const obj = instance;
    const prefix = options.prefix;
    // Set instance properties
    const baseData = {
        type: 'icon-set',
        prefix,
    };
    Object.assign(obj, baseData);
    // Load data
    obj._loadDataFromDirectory = (path) => importIconSetFromJSON(prefix, path, options.filename, options);
    return obj;
}
